CREATE TABLE categoria_movimentacao (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    descricao VARCHAR(45)
);

CREATE TABLE conta ( id INTEGER PRIMARY KEY AUTOINCREMENT, descricao VARCHAR, saldo INTEGER, id_categoria INTEGER, id_banco INTERGER,
FOREIGN KEY (id_categoria) REFERENCES categoriaConta(id), FOREIGN KEY (id_banco) REFERENCES banco(id)
);

CREATE TABLE tipo_movimentacao {
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    descricaoTipoMovimentacao VARCHAR
}

CREATE TABLE categoria_conta {
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    descricaoCategoriaConta VARCHAR
}

CREATE TABLE banco {
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    nomeBanco VARCHAR
}
